const rawContent = $json.choices[0].message.content;
const aiData = JSON.parse(rawContent);
const originalInput = $("Webhook2").first().json.body;

const cleanIntent = aiData.intent ? aiData.intent.trim() : 'update_preferences';
const data = aiData.data || {};

// CORRE√á√ÉO 1: Extrair o objeto macros que est√° DENTRO de data
const macros = data.macros || { protein: 0, carbs: 0, fat: 0 };

let sqlQuery = "";
let tableName = "";

if (cleanIntent === 'feedback') {
    sqlQuery = `INSERT INTO ai_learning (user_id, bad_experience, topic) VALUES ('${originalInput.user_id}', '${data.feedback_content || "Feedback gen√©rico"}', 'Feedback Negativo') RETURNING id;`;
    tableName = "ai_learning";

} else if (cleanIntent === 'planning') {
    sqlQuery = `INSERT INTO workout_plans (user_id, plan_structure, active) VALUES ('${originalInput.user_id}', '${JSON.stringify(data)}', true) RETURNING id;`;
    tableName = "workout_plans";

} else {
    // --- L√ìGICA DE PREFER√äNCIAS OTIMIZADA ---

    // 1. Tratamento para garantir Arrays v√°lidos (evita erros se vier string ou nulo)
    const equipment = JSON.stringify(data.available_equipment || ["Peso do corpo"]);

    // Transforma string √∫nica em array se necess√°rio (ex: "peixe" vira ["peixe"])
    let newDislikedEx = Array.isArray(data.disliked_exercises) ? data.disliked_exercises : [];
    let newDislikedFood = Array.isArray(data.disliked_foods) ? data.disliked_foods : [];

    if (typeof data.disliked_exercises === 'string') newDislikedEx = [data.disliked_exercises];
    if (typeof data.disliked_foods === 'string') newDislikedFood = [data.disliked_foods];

    // Prepara JSON para o SQL
    const jsonNewEx = JSON.stringify(newDislikedEx);
    const jsonNewFood = JSON.stringify(newDislikedFood);

    // 2. SQL Query com MERGE (Adiciona sem apagar o anterior)
    sqlQuery = `
        INSERT INTO user_preferences (user_id, goal, injuries, available_equipment, disliked_exercises, disliked_foods) 
        VALUES (
            '${originalInput.user_id}', 
            '${data.goal || ""}', 
            '${data.injuries || ""}', 
            '${equipment}'::jsonb, 
            '${jsonNewEx}'::jsonb, 
            '${jsonNewFood}'::jsonb
        )
        ON CONFLICT (user_id) 
        DO UPDATE SET 
            -- S√≥ atualiza Objetivo/Les√µes se vier algo novo (diferente de vazio)
            goal = CASE WHEN EXCLUDED.goal <> '' AND EXCLUDED.goal <> 'undefined' THEN EXCLUDED.goal ELSE user_preferences.goal END,
            injuries = CASE WHEN EXCLUDED.injuries <> '' AND EXCLUDED.injuries <> 'undefined' THEN EXCLUDED.injuries ELSE user_preferences.injuries END,

            -- Mescla a lista nova com a antiga (evita sobrescrever)
            available_equipment = CASE WHEN jsonb_array_length(EXCLUDED.available_equipment) > 0 THEN EXCLUDED.available_equipment ELSE user_preferences.available_equipment END,

            disliked_exercises = (
                SELECT jsonb_agg(DISTINCT x) 
                FROM jsonb_array_elements(COALESCE(user_preferences.disliked_exercises, '[]'::jsonb) || EXCLUDED.disliked_exercises) t(x)
            ),
            disliked_foods = (
                SELECT jsonb_agg(DISTINCT x) 
                FROM jsonb_array_elements(COALESCE(user_preferences.disliked_foods, '[]'::jsonb) || EXCLUDED.disliked_foods) t(x)
            );
    `;
    tableName = "user_preferences";
}

return [
  {
    json: {
      user_id: originalInput.user_id,
      intent: cleanIntent,            
      reply_message: aiData.reply_message,       
      generated_sql: sqlQuery,
      target_table: tableName,
      data: data,
      processed_json: JSON.stringify(aiData), 
      calories: data.calories || 0,
      protein: macros.protein || 0,
      carbs: macros.carbs || 0,
      fat: macros.fat || 0
    }
  }
];

